import React, { useEffect, useState } from 'react';
import data from '../data/data';
import Carousel from '../component/carousel/Carousel';
import 'swiper/css';

const Home = () => {
  const [productdata, setproductdata] = useState([]);
  const [showAll, setShowAll] = useState(false);

  useEffect(() => {
    fetch('https://fakestoreapi.com/products/1')
      .then(res => res.json())
      .then(json => setproductdata(json))
      .catch(error => console.error('Error fetching product data:', error));
  }, []);

  const toggleShowAll = () => {
    setShowAll(!showAll);
  };

  const displayedMobiles = showAll ? data.mobilePhones : data.mobilePhones.slice(0, 4);

  return (
    <div className='grid h-auto w-screen my-10'>
      <div className='mx-auto my-2 h-auto'>
        <Carousel />
      </div>

      <div className='grid sm:grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-4'>
        {displayedMobiles.map((mobile) => (
          <div className='mda' key={mobile.name}>
            <div className="mda">
              <h3>{mobile.name}</h3>
            </div>
            <div className="mda">
              <img src="/images/galaxya.jpg" alt={mobile.name} />
            </div>
            <div className="mda">
              {mobile.price}
            </div>
          </div>
        ))}
      </div>

      <div className="text-center mt-4">
        <button onClick={toggleShowAll} className="bg-blue-600 text-white py-2 px-4 rounded">
          {showAll ? 'Show Less' : 'View All'}
        </button>
      </div>
    </div>
  );
};

export default Home;
