// CarouselComponent.jsx
import React, { Component } from "react";
import Slider from "react-slick";
import {slides }from './config'


const Carouselhome = () => {
    const settings = {
        customPaging: function (i) {
            return (
                <a>
                    <img src={slides[i].image} alt={`thumbnail ${i + 1}`} />
                </a>
            );
        },
        dots: true,
        dotsClass: "slick-dots slick-thumb",
        infinite: true,
        speed: 500,
        slidesToShow: 1,
        slidesToScroll: 1
    };
    return (
        <div className="slider-container  grid grri items-center justify-center ">
            <Slider {...settings}>
                {slides.map((slide) => (
                    <div key={slide.id} className="flex">
                        <img src={slide.image} alt={slide.content} />
                    </div>
                ))}
            </Slider>
        </div>
    );
}
export default Carouselhome;
