// App.js
import './index.css';
import './App.css';
import Navbar from './component/navbar/Navbar';
import AdminHomepage from './pages/AdminHomepage';
import AllMobiles from './pages/Allmobiles';
import Home from './pages/Home';
import AllProducts from './pages/adminpages/AllProducts';
import Orders from './pages/adminpages/Orders';
import { BrowserRouter, Route, Routes, useLocation } from 'react-router-dom';
import { AuthProvider, useAuth } from './AuthContext';
import { ProtectedRoute } from './ProtectedRoute';
import AdminLogin from './AdminLogin';
import { useEffect } from 'react';

function App() {
  const location = useLocation();
  const { role, login } = useAuth();

  useEffect(() => {
    // Mock login for demonstration purposes
    login('admin'); // Replace with actual login logic
  }, [login]);

  return (
    <div className="App">
      {location.pathname !== '/admin/santu' && <Navbar />}
      <Routes>
        <Route path='/home' element={<Home />} />
        <Route path='/all-mobiles' element={<AllMobiles />} />
        <Route path='/admin/santu' element={<AdminLogin />} />

        {/* Admin Routes */}
        <Route path='/admin/santu/home' element={<ProtectedRoute role="admin"><AdminHomepage /></ProtectedRoute>}>
          <Route path='all-products' element={<AllProducts />} />
          <Route path='orders' element={<Orders />} />
        </Route>
      </Routes>
    </div>
  );
}

function AppWrapper() {
  return (
    <AuthProvider>
      <BrowserRouter>
        <App />
      </BrowserRouter>
    </AuthProvider>
  );
}

export default AppWrapper;
