import React from "react";
import Slider from "react-slick";

function Responsive() {
    var settings = {
        dots: true,
        infinite: false,
        speed: 500,
        slidesToShow: 1,
        slidesToScroll: 1,
        initialSlide: 0,
        responsive: [
            {
                breakpoint: 1024,
                settings: {
                    slidesToShow: 3,
                    slidesToScroll: 3,
                    infinite: true,
                    dots: true
                }
            },
            {
                breakpoint: 600,
                settings: {
                    slidesToShow: 2,
                    slidesToScroll: 2,
                    initialSlide: 2
                }
            },
            {
                breakpoint: 480,
                settings: {
                    slidesToShow: 1,
                    slidesToScroll: 1
                }
            }
        ]
    };
    return (
        <div className="slider-container grid grid-cols-3">
            <Slider {...settings}>
                <Slider {...settings}>
                    {/* <div className='slider mx-auto'>
                        <img src="/images/carousel/galaxy-s25-ultra.jpg" alt="Galaxy S25 Ultra" />
                    </div> */}
                    <div></div>
                    <div className='slider mx-auto'>
                        <img src="/images/carousel/iphone-15-pro.jpg" alt="iPhone 15 Pro" />
                    </div>
                </Slider>
            </Slider>
        </div>
    );
}

export default Responsive;
