import React, { useState } from 'react'
import navitems from '../data/navdata'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faBars } from '@fortawesome/free-solid-svg-icons'
import { NavLink } from 'react-router-dom'
const Navbar = () => {

  const [shownav, setshownav] = useState(false)

  const hidemenu = () => {
    setshownav(!shownav)
  }

  return (
    <div className=' grid grid-cols-2 md:hidden lg:hidden xl:hidden    bg-slate-800 h-20 p-2 text-white'>

      <div>
        Sm
      </div>
    
      <div className='grid  text-right mx-2'>

      

        <div className='text-right'>
          <button onClick={hidemenu}>
            <FontAwesomeIcon icon={faBars} />
          </button>

        </div>

        {
          shownav && navitems.map((item) => {
            return <div item={item} className='text-left mx-[20%]'>
              <NavLink to={item.path}>
                {
                  item.name
                }
              </NavLink>

            </div>
          })
        }
      </div>

      <div className='sm:hidden grid md:grid-cols-2 gap-1 lg:grid-cols-2  xl:grid-cols-2 '>

        <div>
          Shri Mukundraj mobiles
        </div>

        {
          navitems.map((item) => {
            return (
              <div className='mx-2'>
                <NavLink to={item.path}>
                  {
                    item.name
                  }
                </NavLink>
              </div>
            )
          })
        }


      </div>
    </div>
  )
}

export default Navbar
