import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import data from '../data/data';
import Carousel from '../component/carousel/Carousel';
import 'swiper/css';

import BrandCarousel from '../component/BrandCarousel'

const Home = () => {
  const navigate = useNavigate();
  const [productdata, setproductdata] = useState([]);

  useEffect(() => {
    fetch('https://fakestoreapi.com/products/1')
      .then(res => res.json())
      .then(json => setproductdata(json))
      .catch(error => console.error('Error fetching product data:', error));
  }, []);

  const handleViewAllClick = () => {
    navigate('/all-mobiles');
  };

  return (
    <div className='grid h-auto w-screen my-10 pt-8'> {/* Added padding-top */}
      <div className='mx-auto my-2 h-auto'>
        <Carousel />
      </div>

      <div className='grid sm:grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-4 text-center'>
        {data.mobilePhones.slice(0, 4).map((mobile) => (
          <div className='bg-white shadow-md rounded-lg overflow-hidden' key={mobile.name}>
            <div className="p-4">
              <h3 className="text-lg font-semibold">{mobile.name}</h3>
            </div>
            <div className="p-4">
              <img src="/images/galaxya.jpg" alt={mobile.name} className="w-full h-48 object-cover" />
            </div>
            <div className="p-4">
              <p className="text-gray-600">Price: {mobile.price}</p>
            </div>
          </div>
        ))}
      </div>

      <div className="text-center mt-4">
        <button onClick={handleViewAllClick} className="bg-blue-600 text-white py-2 px-4 rounded">
          View All
        </button>
      </div>


      <div className='w-screen my-3'>
        <BrandCarousel/>
      </div>

    </div>
  );
};

export default Home;
