// CarouselComponent.jsx

import React, { useState } from 'react';
import '../../index.css';

const Carouselhome = () => {
    const [currentIndex, setCurrentIndex] = useState(0);

    const slides = [
        {
            id: 1,
            content: "/images/carousel/galaxy-s25-ultra-features-kv.jpg",
        },
        {
            id: 2,
            content: "Slide 2 Content",
        },
        {
            id: 3,
            content: "Slide 3 Content",
        },
    ];

    const nextSlide = () => {
        setCurrentIndex((currentIndex + 1) % slides.length);
    };

    const prevSlide = () => {
        setCurrentIndex((currentIndex - 1 + slides.length) % slides.length);
    };

    return (
        <div className="relative w-full max-w-2xl mx-auto">
            <div className="overflow-hidden rounded-lg shadow-lg">
                {slides.map((slide, index) => (
                    <div
                        key={slide.id}
                        className={`transition-transform duration-500 ease-in-out transform ${index === currentIndex ? 'translate-x-0' : 'translate-x-full'
                            }`}
                    >
                        <div className="p-8 bg-white text-center">
                            <img src="" alt="" /></div>
                    </div>
                ))}
            </div>
            <button
                className="absolute top-1/2 left-0 transform -translate-y-1/2 bg-gray-800 text-white px-3 py-2 rounded-full"
                onClick={prevSlide}
            >
                &#10094;
            </button>
            <button
                className="absolute top-1/2 right-0 transform -translate-y-1/2 bg-gray-800 text-white px-3 py-2 rounded-full"
                onClick={nextSlide}
            >
                &#10095;
            </button>
        </div>
    );
};

export default Carouselhome;
