import React from 'react';
import { Swiper, SwiperSlide } from 'swiper/react';
import { Navigation, Pagination, Scrollbar, Autoplay } from 'swiper/modules';
import 'swiper/css';
import 'swiper/css/navigation';
import 'swiper/css/pagination';
import 'swiper/css/scrollbar';

const BrandCarousel = () => {
    const brandImages = [
        { id: 1, name: 'Apple', src:'/images/brands/boat-logo.jpg' },
        { id: 2, name: 'Samsung', src: '/images/brands/boat-logo.jpg' },
        { id: 3, name: 'Sony', src: '/images/brands/boat-logo.jpg' },
        { id: 4, name: 'Boat', src: '/images/brands/boat-logo.jpg' },
        { id: 5, name: 'Xiaomi', src: '/images/brands/mi-logo.png' },

        { id: 6, name: 'Realme', src: '/images/brands/mi-logo.png' },
        // Add more brands as needed
    ];

    return (
        <div className="h-[60vh] overflow-hidden relative">
            <Swiper
                modules={[Navigation, Pagination, Scrollbar, Autoplay]}
                navigation
                pagination={{ clickable: true }}
                scrollbar={{ draggable: true }}
                autoplay={{
                    delay: 3000, // 3 seconds
                    disableOnInteraction: false,
                }}
                loop
                className="h-full"
            >
                {brandImages.map((brand, index) => (
                    <SwiperSlide key={index} className="flex flex-col items-center justify-center">
                        <img src={`https://www.pexels.com/search/brand/${brand.name}`} alt={brand.name} className="w-48 h-48 object-contain" />
                        <span className="mt-2 text-lg font-semibold">{brand.name}</span>
                    </SwiperSlide>
                ))}
            </Swiper>
        </div>
    );
};

export default BrandCarousel;
