import React from 'react'
import { Swiper, SwiperSlide } from 'swiper/react'

import { Navigation, Pagination, Scrollbar, Autoplay } from 'swiper/modules';
import 'swiper/css'

import 'swiper/css/navigation';
import 'swiper/css/pagination';
import 'swiper/css/scrollbar';



const Carousel = () => {
  return (
      <div className='swiper-container '>
          
          <Swiper
              modules={[Navigation, Pagination, Scrollbar, Autoplay]}
              navigation
              pagination={{ clickable: true }}
              scrollbar={{ draggable: true }}
              autoplay={{
                  delay: 3000, // 3 seconds
                  disableOnInteraction: false,
              }}
              loop
          >
              <SwiperSlide>
                  <img src="/images/carousel/iphone-15-pro.jpg" alt="iphone 15 pro" />
              </SwiperSlide>

              <SwiperSlide>n
                  <img src="/images/carousel/samsung-phones.jpeg" alt="" />
              </SwiperSlide>
              <SwiperSlide>
                  <img src="/images/carousel/iphone-15-pro.jpg" alt="" />
              </SwiperSlide>
              <SwiperSlide>
                  <img src="/images/carousel/Samsung-Galaxy-S25-Ultra.webp" alt="" />
              </SwiperSlide>
              <SwiperSlide>
                  <img src="/images/carousel/iphone-15-pro.jpg" alt="" />
              </SwiperSlide>


</Swiper>
          
    </div>
  )
}

export default Carousel
