import React, { useRef, useEffect } from 'react';
import 'swiper/css';

const images = [
    "/images/carousel/iphone-15-pro.jpg",
    "/images/carousel/samsung-phones.jpeg",
    "/images/carousel/iphone-15-pro.jpg",
    "/images/carousel/Samsung-Galaxy-S25-Ultra.webp",
    "/images/carousel/iphone-15-pro.jpg"
];

const Carousel = () => {
    const swiperRef = useRef(null);
    let currentSlide = 0;

   

    useEffect(() => {
        const interval = setInterval(() => {
            if (swiperRef.current) {
                currentSlide = (currentSlide + 1) % images.length;
                swiperRef.current.style.transform = `translateX(-${currentSlide * 100}%)`;
            }
        }, 3000); // 3 seconds

        return () => clearInterval(interval);
    }, []);

    return (
        <div className="sm:h-[60vh]  overflow-hidden relative xl:h-[70vh] lg:h-[70vh] ">
            <div
                ref={swiperRef}
                className="flex transition-transform duration-500"
            >
                {images.map((src, index) => (
                    <div key={index} className="min  grid">
                        <img src={src} alt={`Slide ${index + 1}`} className="w-full h-[100%] object-cover" style={{objectFit:''}} />
                    </div>
                ))}
            </div>
        </div>
    );
}

export default Carousel;
